import 'package:dio/dio.dart';

class PurchaseOrderApi {
  final Dio dio;

  PurchaseOrderApi(this.dio);

  Future<void> createPurchaseOrder(PurchaseOrder order) async {
    await dio.post('/purchase-orders', data: _mapFromEntity(order));
  }

  Future<void> updatePurchaseOrder(PurchaseOrder order) async {
    await dio.put('/purchase-orders/${order.id}', data: _mapFromEntity(order));
  }

  Future<void> deletePurchaseOrder(String id) async {
    await dio.delete('/purchase-orders/$id');
  }

  Future<PurchaseOrder?> getPurchaseOrderById(String id) async {
    final res = await dio.get('/purchase-orders/$id');
    if (res.data == null) return null;
    return _mapToEntity(res.data as Map<String, dynamic>);
  }

  Future<List<PurchaseOrder>> getAllPurchaseOrders() async {
    final res = await dio.get('/purchase-orders');
    return (res.data as List)
        .map((json) => _mapToEntity(json as Map<String, dynamic>))
        .toList();
  }

  Future<PurchaseOrder> approvePurchaseOrder(String id, String approverUserId) async {
    final res = await dio.post('/purchase-orders/$id/approve', data: {
      'approvedByUserId': approverUserId,
    });
    return _mapToEntity(res.data as Map<String, dynamic>);
  }

  Future<PurchaseOrder> cancelPurchaseOrder(String id, String reason) async {
    final res = await dio.post('/purchase-orders/$id/cancel', data: {
      'reason': reason,
    });
    return _mapToEntity(res.data as Map<String, dynamic>);
  }

  Future<List<PurchaseOrder>> filterPurchaseOrders(String? status, String? supplierId) async {
    final res = await dio.get('/purchase-orders', queryParameters: {
      if (status != null) 'status': status,
      if (supplierId != null) 'supplierId': supplierId,
    });
    return (res.data as List)
        .map((json) => _mapToEntity(json as Map<String, dynamic>))
        .toList();
  }

  /// -------- Mapping Helpers -------- ///

  Map<String, dynamic> _mapFromEntity(PurchaseOrder order) {
    return {
      'id': order.id,
      'supplierId': order.supplierId,
      'createdByUserId': order.createdByUserId,
      'approvedByUserId': order.approvedByUserId,
      'goodsReceiptId': order.goodsReceiptId,
      'invoiceId': order.invoiceId,
      'totalAmount': order.totalAmount,
      'attachments': order.attachments,
      'createdAt': order.createdAt.toIso8601String(),
      'expectedDeliveryDate': order.expectedDeliveryDate?.toIso8601String(),
      'receivedDate': order.receivedDate?.toIso8601String(),
      'status': order.status,
      'items': order.items.map((item) => {
        'stockId': item.stockId,
        'quantityOrdered': item.quantityOrdered,
        'quantityReceived': item.quantityReceived,
        'unitCost': item.unitCost,
      }).toList(),
      'notes': order.notes,
    };
  }

  PurchaseOrder _mapToEntity(Map<String, dynamic> json) {
    return PurchaseOrder(
      id: json['id'] as String,
      supplierId: json['supplierId'] as String,
      createdByUserId: json['createdByUserId'] as String,
      approvedByUserId: json['approvedByUserId'] as String?,
      goodsReceiptId: json['goodsReceiptId'] as String?,
      invoiceId: json['invoiceId'] as String?,
      totalAmount: (json['totalAmount'] as num).toDouble(),
      attachments: (json['attachments'] as List?)?.map((a) => a as String).toList(),
      createdAt: DateTime.parse(json['createdAt']),
      expectedDeliveryDate: json['expectedDeliveryDate'] != null
          ? DateTime.parse(json['expectedDeliveryDate'])
          : null,
      receivedDate: json['receivedDate'] != null
          ? DateTime.parse(json['receivedDate'])
          : null,
      status: json['status'] as String,
      items: (json['items'] as List).map((item) {
        final map = item as Map<String, dynamic>;
        return PurchaseOrderItem(
          stockId: map['stockId'] as String,
          quantityOrdered: map['quantityOrdered'] as int,
          quantityReceived: map['quantityReceived'] as int?,
          unitCost: (map['unitCost'] as num).toDouble(),
        );
      }).toList(),
      notes: json['notes'] as String?,
    );
  }
}

