class MainScaffold extends ConsumerStatefulWidget {
  final Widget child;
  const MainScaffold({super.key, required this.child});

  @override
  ConsumerState<MainScaffold> createState() => _MainScaffoldState();
}

class _MainScaffoldState extends ConsumerState<MainScaffold> {
  late int _selectedIndex;

  // ValueNotifier controls SpeedDial open/close and overlay visibility
  final ValueNotifier<bool> _dialOpen = ValueNotifier<bool>(false);

  static const List<_NavItem> _navItems = [
    _NavItem(icon: Icons.inventory, label: 'Stock', route: '/stock'),
    _NavItem(icon: Icons.receipt_long, label: 'Purchase', route: '/purchase-order'),
    _NavItem(icon: Icons.insert_chart_outlined, label: 'Reporting', route: '/reporting'),
    _NavItem(icon: Icons.timeline, label: 'Activities', route: '/activity'),
  ];

  @override
  void didChangeDependencies() {
    super.didChangeDependencies();
    // Use GoRouter location for robust current-path detection
    final loc = GoRouter.of(context).location; // e.g. "/stock/123"
    final idx = _navItems.indexWhere((i) => loc.startsWith(i.route));
    _selectedIndex = idx == -1 ? 0 : idx;
  }

  @override
  void dispose() {
    _dialOpen.dispose();
    super.dispose();
  }

  void _onItemTapped(int index) {
    if (_selectedIndex == index) return;
    setState(() => _selectedIndex = index);
    context.go(_navItems[index].route);
  }

  @override
  Widget build(BuildContext context) {
    final theme = Theme.of(context);

    // Layout constants (small, tidy)
    const double navHeight = 60.0;
    const double indicatorWidth = 60.0;
    const double indicatorHeight = 34.0;
    const double iconSize = 18.0;
    const double fabDiameter = 48.0;
    const double fabIconSize = 18.0;
    final Color activeColor = theme.colorScheme.primary;
    final Color inactiveColor = theme.colorScheme.onSurface.withOpacity(0.68);

    return Scaffold(
      // Body in a Stack so custom overlay sits above content
      body: Stack(
        children: [
          widget.child,

          // Frosted overlay when SpeedDial is open (subtle glass)
          ValueListenableBuilder<bool>(
            valueListenable: _dialOpen,
            builder: (context, open, _) {
              if (!open) return const SizedBox.shrink();
              return Positioned.fill(
                child: GestureDetector(
                  behavior: HitTestBehavior.opaque,
                  onTap: () => _dialOpen.value = false,
                  child: AnimatedOpacity(
                    duration: const Duration(milliseconds: 200),
                    opacity: 1.0,
                    child: BackdropFilter(
                      // reduced blur for a lighter glass effect
                      filter: ImageFilter.blur(sigmaX: 3.5, sigmaY: 3.5),
                      child: Container(
                        color: theme.colorScheme.surface.withOpacity(0.12), // slight tint
                      ),
                    ),
                  ),
                ),
              );
            },
          ),
        ],
      ),

      // main floating action (SpeedDial)
      floatingActionButton: _buildFab(theme, fabDiameter, fabIconSize),
      floatingActionButtonLocation: FloatingActionButtonLocation.miniEndFloat,

      // Bottom navigation bar — frosted, compact, minimal
      bottomNavigationBar: SafeArea(
        top: false,
        child: Padding(
          padding: const EdgeInsets.fromLTRB(12, 8, 12, 8),
          child: ClipRRect(
            borderRadius: BorderRadius.circular(14),
            child: BackdropFilter(
              // subtle blur on the bottom bar
              filter: ImageFilter.blur(sigmaX: 3.0, sigmaY: 3.0),
              child: Material(
                color: theme.colorScheme.surface.withOpacity(0.92),
                child: BottomAppBar(
                  elevation: 0,
                  notchMargin: 8,
                  shape: const CircularNotchedRectangle(),
                  color: Colors.transparent,
                  child: SizedBox(
                    height: navHeight,
                    child: Stack(
                      alignment: Alignment.center,
                      children: [
                        // Moving rounded indicator behind the active item
                        AnimatedAlign(
                          alignment: _alignmentForIndex(_selectedIndex, _navItems.length),
                          duration: const Duration(milliseconds: 300),
                          curve: Curves.easeOutCubic,
                          child: Padding(
                            padding: const EdgeInsets.only(bottom: 6),
                            child: AnimatedContainer(
                              duration: const Duration(milliseconds: 220),
                              width: indicatorWidth,
                              height: indicatorHeight,
                              decoration: BoxDecoration(
                                color: activeColor.withOpacity(0.10),
                                borderRadius: BorderRadius.circular(indicatorHeight / 2),
                              ),
                            ),
                          ),
                        ),

                        // Nav items (equally spaced)
                        Row(
                          mainAxisAlignment: MainAxisAlignment.spaceAround,
                          children: List.generate(_navItems.length, (i) {
                            final item = _navItems[i];
                            final selected = i == _selectedIndex;
                            return _BottomNavItem(
                              icon: item.icon,
                              label: item.label,
                              selected: selected,
                              activeColor: activeColor,
                              inactiveColor: inactiveColor,
                              onTap: () => _onItemTapped(i),
                              iconSize: iconSize,
                            );
                          }),
                        ),
                      ],
                    ),
                  ),
                ),
              ),
            ),
          ),
        ),
      ),
    );
  }

  // FAB / SpeedDial builder
  Widget _buildFab(ThemeData theme, double diameter, double iconSize) {
    final actions = <_ActionItem>[
      _ActionItem(icon: LucideIcons.scan, label: 'Scan Barcode', route: '/scan'),
      _ActionItem(icon: Icons.inventory_2_outlined, label: 'New Stock Take', route: '/new-stock-take'),
      _ActionItem(icon: Icons.precision_manufacturing, label: 'New Production Run', route: '/new-production'),
      _ActionItem(icon: Icons.article_outlined, label: 'New Bill of Material', route: '/new-bom'),
      _ActionItem(icon: Icons.shopping_cart_outlined, label: 'New Customer Order', route: '/new-order'),
    ];

    Widget _buildLabel(BuildContext ctx, IconData icon, String label) {
      final t = Theme.of(ctx);
      return ClipRRect(
        borderRadius: BorderRadius.circular(12),
        child: BackdropFilter(
          filter: ImageFilter.blur(sigmaX: 6, sigmaY: 6),
          child: Container(
            padding: const EdgeInsets.symmetric(vertical: 8, horizontal: 12),
            color: t.colorScheme.surface.withOpacity(0.65),
            child: Row(
              mainAxisSize: MainAxisSize.min,
              children: [
                Icon(icon, size: 16, color: t.colorScheme.onSurface.withOpacity(0.95)),
                const SizedBox(width: 10),
                Text(label, style: TextStyle(fontSize: 14, fontWeight: FontWeight.w600, color: t.colorScheme.onSurface)),
              ],
            ),
          ),
        ),
      );
    }

    final sdChildren = actions.map((a) {
      return SpeedDialChild(
        child: const SizedBox.shrink(), // we only show labelWidget
        labelWidget: _buildLabel(context, a.icon, a.label),
        backgroundColor: Colors.transparent,
        labelBackgroundColor: Colors.transparent,
        onTap: () {
          _dialOpen.value = false;
          GoRouter.of(context).push(a.route);
        },
      );
    }).toList();

    final fabChild = Container(
      width: diameter,
      height: diameter,
      decoration: BoxDecoration(
        color: theme.colorScheme.primary,
        shape: BoxShape.circle,
        boxShadow: [BoxShadow(color: Colors.black.withOpacity(0.12), blurRadius: 8, offset: const Offset(0, 3))],
      ),
      child: Center(child: Icon(LucideIcons.plus, size: iconSize, color: Colors.white)),
    );

    final activeChild = Container(
      width: diameter,
      height: diameter,
      decoration: BoxDecoration(
        color: theme.colorScheme.primary,
        shape: BoxShape.circle,
      ),
      child: Center(child: Icon(Icons.close, size: iconSize, color: Colors.white)),
    );

    return SpeedDial(
      icon: null,
      child: fabChild,
      activeChild: activeChild,
      openCloseDial: _dialOpen,
      onOpen: () => _dialOpen.value = true,
      onClose: () => _dialOpen.value = false,
      backgroundColor: Colors.transparent,
      foregroundColor: Colors.white,
      overlayOpacity: 0.0, // we manage our own overlay
      curve: Curves.easeInOut,
      elevation: 0,
      shape: const CircleBorder(),
      useRotationAnimation: true,
      animationDuration: const Duration(milliseconds: 240),
      children: sdChildren,
    );
  }

  Alignment _alignmentForIndex(int index, int total) {
    if (total <= 1) return Alignment.center;
    final frac = index / (total - 1);
    final ax = (frac * 2) - 1;
    return Alignment(ax, 0);
  }
}

